      newFrags[i].TextToSpeak.Replace(term.US, term.UK);  
      }  
      //Generate progress meter events if supported  
      if ((site.EventInterest & WordBoundaryFlag) != 0) {  
      string[] subs = newFrags[i].TextToSpeak.Split(spaces);  
  
      foreach (string s in subs) {  
        int offset = newFrags[i].TextOffset;  
        SpeechEventInfo spEvent = new SpeechEventInfo((Int16)TtsEventId.WordBoundary,   
                (Int16)EventParameterType.Undefined,   
                 s.Length, new IntPtr(offset));  
        offset += s.Length;  
        if (s.Trim().Length > 0) {  
          SpeechEventInfo[] events = new SpeechEventInfo[1];  
          events[0] = spEvent;  
          site.AddEvents(events, 1);  
        }  
      }  
      }  
    }  
  }  
  
  _baseSynthesize.Speak(newFrags, wfx, site);  
  
}  
```

  
  
## Remarks  
 <span data-ttu-id="057f4-143">Der Standardwert dieser Eigenschaft ist `System.String.Empty`.</span><span class="sxs-lookup"><span data-stu-id="057f4-143">The default value of this property is `System.String.Empty`.</span></span>  
  
 <span data-ttu-id="057f4-144">Anwendungen können den Wert von <xref:System.Speech.Synthesis.TtsEngine.TextFragment.TextToSpeak%2A>vollständig ändern.</span><span class="sxs-lookup"><span data-stu-id="057f4-144">Applications are free to completely change the value of <xref:System.Speech.Synthesis.TtsEngine.TextFragment.TextToSpeak%2A>.</span></span>  
  
 <span data-ttu-id="057f4-145">Wenn Sie den Wert von <xref:System.Speech.Synthesis.TtsEngine.TextFragment.TextToSpeak%2A> zurücksetzen, wird der Wert von <xref:System.Speech.Synthesis.TtsEngine.TextFragment.TextOffset%2A> und <xref:System.Speech.Synthesis.TtsEngine.TextFragment.TextLength%2A>nicht geändert.</span><span class="sxs-lookup"><span data-stu-id="057f4-145">Resetting the value of <xref:System.Speech.Synthesis.TtsEngine.TextFragment.TextToSpeak%2A> will not change the value of <xref:System.Speech.Synthesis.TtsEngine.TextFragment.TextOffset%2A> and <xref:System.Speech.Synthesis.TtsEngine.TextFragment.TextLength%2A>.</span></span>  
  
   
  
